mapper.select:
SELECT COUNT(*)
FROM LESSON_ENROLL
WHERE USER_NO = #{userNo}
AND SUBJECT = #{subject}
AND STATUS ='Y'

mapper.update:
UPDATE LESSON_ENROLL
SET STATUS = 'N'
WHERE LE_NO =#{leNo}

mapper.select:
SELECT COUNT(*)
FROM LESSON
WHERE STUDENT_NO = #{studentNo}
AND SUBJECT = #{subject}
AND TEACHER_NO = #{teacherNo}
AND STATUS ='N'

SELECT *
FROM LESSON_REVIEW
WHERE REF_LENO = #{refLeno}
AND RE_UNAME = #{reUname}

mapper.update:
UPDATE LESSON_REVIEW
SET RE_TEXT = #{reText}
WHERE REF_LENO = #{refLeno}
AND RE_UNO = #{reUno}
