mapper.insert:
INSERT INTO SOLUTION(
				  SOLUTION_NO
				 ,SUBJECT
				 ,TAG
				 ,USER_NO
				 ,TITLE
				 ,SOLUTION_CONTENTS
				 )
VALUES(
		SEQ_SNO.NEXTVAL
		,#{subject}
		,#{tag}
		,#{userNo}
		,#{title}
		,#{solutionContents}
		)

mapper.update:
UPDATE SOLUTION
SET STATUS='N'
WHERE SOLUTION_NO = #{solutionNo}

mapper.insert:
INSERT INTO SOLUTION_FILES(
				  SF_NO
				 ,SF_ORIGINNAME
				 ,SF_SYSNAME
				 ,SOLUTION_NO
				 )
VALUES(
		SEQ_SFNO.NEXTVAL
		,#{sfOriginName}
		,#{sfSysName}
		,#{solutionNo}
		)

mapper.update:
UPDATE SOLUTION_FILES
SET SOLUTION_NO = #{solutionNo}
WHERE SF_SYSNAME = #{sfSysName}

UPDATE SOLUTION
SET TITLE = #{title},	
	SOLUTION_CONTENTS = #{solutionContents},
	SUBJECT = #{subject},
	TAG = #{tag}
WHERE SOLUTION_NO = #{solutionNo}
